+          child: Column(
+            crossAxisAlignment: CrossAxisAlignment.start,
+            children: [
+              Text('Hijri Date:', style: TextStyle(color: AppColors.theme)),
+              Text('${day.hijriWeekday}, ${day.hijriMonth} ${day.hijriDay}, ${day.hijriYear}'),
